import React, { useState } from 'react';
import axios from 'axios';

const Payments = () => {
  const [form, setForm] = useState({
    loanId: '',
    amount: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/payments`, form);
      setMessage('Pago realizado con éxito.');
    } catch (error) {
      setMessage('Error al realizar el pago.');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Pagos</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="loanId" className="form-label">ID del Préstamo</label>
          <input
            type="text"
            className="form-control"
            id="loanId"
            name="loanId"
            value={form.loanId}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="amount" className="form-label">Cantidad a Pagar</label>
          <input
            type="number"
            className="form-control"
            id="amount"
            name="amount"
            value={form.amount}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Realizar Pago</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Payments;
