import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [loans, setLoans] = useState([]);
  const [clients, setClients] = useState([]);
  const [recentPayments, setRecentPayments] = useState([]);
  const [editLoan, setEditLoan] = useState(null);

  useEffect(() => {
    fetchLoans();
    fetchClients();
    fetchRecentPayments();
  }, []);

  const fetchLoans = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/loans');
      setLoans(response.data);
    } catch (error) {
      console.error('Error fetching loans:', error);
    }
  };

  const fetchClients = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/clients');
      setClients(response.data);
    } catch (error) {
      console.error('Error fetching clients:', error);
    }
  };

  const fetchRecentPayments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/payments');
      setRecentPayments(response.data);
    } catch (error) {
      console.error('Error fetching recent payments:', error);
    }
  };

  const handleDeleteClient = async (clientId) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este cliente?')) {
      try {
        await axios.delete(`http://localhost:5000/api/clients/${clientId}`);
        fetchClients(); // Recargar la lista de clientes después de eliminar
      } catch (error) {
        console.error('Error deleting client:', error);
      }
    }
  };

  return (
    <div>
      <h2>Dashboard</h2>
      <h3>Clientes totales</h3>
      <div className="card bg-info text-white">
        <div className="card-body">
          <h4 className="card-title">Clientes totales</h4>
          <p className="card-text">{clients.length}</p>
        </div>
      </div>
      <h3>Total de Préstamos</h3>
      <div className="card bg-success text-white">
        <div className="card-body">
          <h4 className="card-title">Total de Préstamos</h4>
          <p className="card-text">{loans.length}</p>
        </div>
      </div>
      <h3>Préstamos en Mora</h3>
      <div className="card bg-danger text-white">
        <div className="card-body">
          <h4 className="card-title">Préstamos en Mora</h4>
          <p className="card-text">{loans.filter(loan => loan.status === 'mora').length}</p>
        </div>
      </div>
      <h2>Pagos Recientes</h2>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Cliente</th>
            <th>Monto</th>
            <th>Fecha</th>
          </tr>
        </thead>
        <tbody>
          {recentPayments.map((payment) => (
            <tr key={payment._id}>
              <td>{payment.clientId.name}</td>
              <td>{payment.amount}</td>
              <td>{new Date(payment.date).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Buscar Cliente</h2>
      <input type="text" placeholder="Buscar por nombre o cédula" />
      <button>Buscar</button>
      <h2>Resultados de la Búsqueda</h2>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Correo Electrónico</th>
            <th>Teléfono</th>
            <th>Cédula</th>
            <th>Dirección</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {clients.map((client) => (
            <tr key={client._id}>
              <td>{client.name}</td>
              <td>{client.email}</td>
              <td>{client.phone}</td>
              <td>{client.cedula}</td>
              <td>{client.address}</td>
              <td>
                <button className="btn btn-warning mr-2" onClick={() => handleEditClient(client)}>Editar</button>
                <button className="btn btn-danger" onClick={() => handleDeleteClient(client._id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Préstamos</h2>
      <button className="btn btn-info mb-2" onClick={fetchLoans}>Cargar Préstamos</button>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Cliente</th>
            <th>Monto</th>
            <th>Tasa de Interés</th>
            <th>Plazo</th>
            <th>Frecuencia</th>
            <th>Estado</th>
            <th>Fecha de Vencimiento</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {loans.map((loan) => (
            <tr key={loan._id}>
              <td>{loan.clientId.name}</td>
              <td>{loan.amount}</td>
              <td>{loan.interestRate}</td>
              <td>{loan.term}</td>
              <td>{loan.frequency}</td>
              <td>{loan.status}</td>
              <td>{new Date(loan.dueDate).toLocaleDateString()}</td>
              <td>
                <button className="btn btn-warning mr-2" onClick={() => handleEditLoan(loan)}>Editar</button>
                <button className="btn btn-danger" onClick={() => handleDeleteLoan(loan._id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {editLoan && (
        <div>
          <h2>Editar Préstamo</h2>
          <form onSubmit={handleUpdateLoan}>
            <div className="form-group">
              <label>Monto</label>
              <input
                type="number"
                className="form-control"
                value={editLoan.amount}
                onChange={(e) => setEditLoan({ ...editLoan, amount: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Tasa de Interés</label>
              <input
                type="number"
                className="form-control"
                value={editLoan.interestRate}
                onChange={(e) => setEditLoan({ ...editLoan, interestRate: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Plazo</label>
              <input
                type="number"
                className="form-control"
                value={editLoan.term}
                onChange={(e) => setEditLoan({ ...editLoan, term: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Frecuencia</label>
              <select
                className="form-control"
                value={editLoan.frequency}
                onChange={(e) => setEditLoan({ ...editLoan, frequency: e.target.value })}>
                <option value="daily">Diario</option>
                <option value="weekly">Semanal</option>
                <option value="quincenal">Quincenal</option>
                <option value="monthly">Mensual</option>
              </select>
            </div>
            <div className="form-group">
              <label>Estado</label>
              <input
                type="text"
                className="form-control"
                value={editLoan.status}
                onChange={(e) => setEditLoan({ ...editLoan, status: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Fecha de Vencimiento</label>
              <input
                type="date"
                className="form-control"
                value={new Date(editLoan.dueDate).toISOString().split('T')[0]}
                onChange={(e) => setEditLoan({ ...editLoan, dueDate: e.target.value })} />
            </div>
            <button type="submit" className="btn btn-success">Actualizar</button>
            <button type="button" className="btn btn-secondary ml-2" onClick={() => setEditLoan(null)}>Cancelar</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Dashboard;





