import React, { useState } from 'react';
import axios from 'axios';

const LoanRequest = () => {
  const [form, setForm] = useState({
    customerId: '',
    amount: '',
    term: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/loans/request`, form);
      setMessage('Solicitud de préstamo enviada con éxito.');
    } catch (error) {
      setMessage('Error al enviar la solicitud de préstamo.');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Solicitud de Préstamo</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="customerId" className="form-label">ID del Cliente</label>
          <input
            type="text"
            className="form-control"
            id="customerId"
            name="customerId"
            value={form.customerId}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="amount" className="form-label">Cantidad</label>
          <input
            type="number"
            className="form-control"
            id="amount"
            name="amount"
            value={form.amount}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="term" className="form-label">Plazo</label>
          <input
            type="text"
            className="form-control"
            id="term"
            name="term"
            value={form.term}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Solicitar Préstamo</button>
      </form>
      {message && <p className="mt-3">{message}</p>}
    </div>
  );
};

export default LoanRequest;
