import React, { useState } from 'react';
import axios from 'axios';

const Amortization = () => {
  const [loanId, setLoanId] = useState('');
  const [amortizationSchedule, setAmortizationSchedule] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/loans/${loanId}/amortization`);
      setAmortizationSchedule(response.data);
    } catch (error) {
      console.error('Error fetching amortization schedule', error);
    }
  };

  return (
    <div className="container mt-5">
      <h1>Amortización del Préstamo</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="loanId" className="form-label">ID del Préstamo</label>
          <input
            type="text"
            className="form-control"
            id="loanId"
            value={loanId}
            onChange={(e) => setLoanId(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Obtener Amortización</button>
      </form>
      {amortizationSchedule.length > 0 && (
        <table className="table mt-5">
          <thead>
            <tr>
              <th>Fecha</th>
              <th>Cuota</th>
              <th>Interés</th>
              <th>Principal</th>
              <th>Balance</th>
            </tr>
          </thead>
          <tbody>
            {amortizationSchedule.map((payment, index) => (
              <tr key={index}>
                <td>{payment.date}</td>
                <td>{payment.payment}</td>
                <td>{payment.interest}</td>
                <td>{payment.principal}</td>
                <td>{payment.balance}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Amortization;
