import React from 'react';
import { Table, Button } from 'react-bootstrap';

const Loans = () => {
  // Datos de ejemplo para los préstamos
  const loans = [
    { id: 1, user: 'John Doe', amount: '$1000', status: 'Approved' },
    { id: 2, user: 'Jane Doe', amount: '$2000', status: 'Pending' },
  ];

  return (
    <div>
      <h1>Loans</h1>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>User</th>
            <th>Amount</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {loans.map((loan) => (
            <tr key={loan.id}>
              <td>{loan.id}</td>
              <td>{loan.user}</td>
              <td>{loan.amount}</td>
              <td>{loan.status}</td>
              <td>
                <Button variant="warning">Edit</Button>{' '}
                <Button variant="danger">Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default Loans;
