import React, { useState, useEffect } from "react";
import axios from "axios";

const LoanRequest = () => {
  const [form, setForm] = useState({
    clientId: "",
    amount: "",
    interestRate: "",
    term: "",
    frequency: "monthly",
  });
  const [clients, setClients] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    axios.get("/api/clients").then((response) => {
      setClients(response.data);
    });
  }, []);

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { clientId, amount, interestRate, term, frequency } = form;
    const principal = parseFloat(amount);
    const annualRate = parseFloat(interestRate) / 100;
    let n, rate;

    switch (frequency) {
      case "daily":
        n = term;
        rate = annualRate / 365;
        break;
      case "weekly":
        n = term * 4;
        rate = annualRate / 52;
        break;
      case "biweekly":
        n = term * 2;
        rate = annualRate / 26;
        break;
      case "monthly":
        n = term;
        rate = annualRate / 12;
        break;
      default:
        n = term;
        rate = annualRate / 365;
    }

    const dueDate = new Date();
    if (frequency === 'daily') {
        dueDate.setDate(dueDate.getDate() + n);
    } else if (frequency === 'weekly') {
        dueDate.setDate(dueDate.getDate() + n * 7);
    } else if (frequency === 'biweekly') {
        dueDate.setDate(dueDate.getDate() + n * 14);
    } else if (frequency === 'monthly') {
        dueDate.setMonth(dueDate.getMonth() + n);
    } else if (frequency === 'yearly') {
        dueDate.setFullYear(dueDate.getFullYear() + n);
    }

    const loan = {
      clientId,
      amount,
      interestRate,
      term,
      frequency,
      dueDate: dueDate.toISOString(),
      status: "active",
    };

    try {
      await axios.post("/api/loans", loan);
      setMessage("Préstamo registrado con éxito");
    } catch (error) {
      setMessage("Error al registrar el préstamo");
    }
  };

  return (
    <div className="container mt-5">
      <div className="jumbotron text-center">
        <h1 className="display-4">Solicitud de Préstamo</h1>
        <p className="lead">
          Complete el formulario para solicitar un préstamo
        </p>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="clientId" className="form-label">
            Cliente
          </label>
          <select
            className="form-control"
            id="clientId"
            name="clientId"
            value={form.clientId}
            onChange={handleChange}
            required
          >
            <option value="">Seleccione un cliente</option>
            {clients.map((client) => (
              <option key={client._id} value={client._id}>
                {client.name} ({client.email})
              </option>
            ))}
          </select>
        </div>
        <div className="mb-3">
          <label htmlFor="amount" className="form-label">
            Monto
          </label>
          <input
            type="number"
            className="form-control"
            id="amount"
            name="amount"
            value={form.amount}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="interestRate" className="form-label">
            Tasa de Interés (%)
          </label>
          <input
            type="number"
            className="form-control"
            id="interestRate"
            name="interestRate"
            value={form.interestRate}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="term" className="form-label">
            Término (meses)
          </label>
          <input
            type="number"
            className="form-control"
            id="term"
            name="term"
            value={form.term}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="frequency" className="form-label">
            Frecuencia
          </label>
          <select
            className="form-control"
            id="frequency"
            name="frequency"
            value={form.frequency}
            onChange={handleChange}
            required
          >
            <option value="daily">Diario</option>
            <option value="weekly">Semanal</option>
            <option value="biweekly">Quincenal</option>
            <option value="monthly">Mensual</option>
          </select>
        </div>
        <button type="submit" className="btn btn-primary">
          Solicitar Préstamo
        </button>
      </form>
      {message && <div className="alert alert-info mt-3">{message}</div>}
    </div>
  );
};

export default LoanRequest;
