import React, { useState } from 'react';

const Amortization = () => {
    const [form, setForm] = useState({
        principal: '',
        annualInterestRate: '',
        term: '',
    });
    const [schedule, setSchedule] = useState([]);

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const principal = parseFloat(form.principal);
        const annualInterestRate = parseFloat(form.annualInterestRate) / 100 / 12;
        const term = parseInt(form.term);

        const payments = [];
        let balance = principal;

        for (let i = 0; i < term; i++) {
            const interest = balance * annualInterestRate;
            const principalPayment = (principal / term);
            balance -= principalPayment;

            payments.push({
                month: i + 1,
                interest: interest.toFixed(2),
                principal: principalPayment.toFixed(2),
                balance: balance.toFixed(2),
            });
        }

        setSchedule(payments);
    };

    return (
        <div className="container mt-5">
            <h1>Cálculo de Amortización</h1>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="principal" className="form-label">Monto del Préstamo</label>
                    <input
                        type="number"
                        className="form-control"
                        id="principal"
                        name="principal"
                        value={form.principal}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="annualInterestRate" className="form-label">Tasa de Interés Anual (%)</label>
                    <input
                        type="number"
                        className="form-control"
                        id="annualInterestRate"
                        name="annualInterestRate"
                        value={form.annualInterestRate}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="term" className="form-label">Término (meses)</label>
                    <input
                        type="number"
                        className="form-control"
                        id="term"
                        name="term"
                        value={form.term}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit" className="btn btn-primary">Calcular</button>
            </form>
            <h2 className="mt-5">Amortización</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Mes</th>
                        <th>Interés</th>
                        <th>Principal</th>
                        <th>Balance</th>
                    </tr>
                </thead>
                <tbody>
                    {schedule.map((payment) => (
                        <tr key={payment.month}>
                            <td>{payment.month}</td>
                            <td>{payment.interest}</td>
                            <td>{payment.principal}</td>
                            <td>{payment.balance}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Amortization;
