import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const [summary, setSummary] = useState({});
  const [recentPayments, setRecentPayments] = useState([]);
  const [clientes, setClientes] = useState([]);
  const [loans, setLoans] = useState([]);
  const [overdueLoans, setOverdueLoans] = useState([]);
  const [lateLoans, setLateLoans] = useState([]);

  useEffect(() => {
    fetchSummary();
    fetchRecentPayments();
    fetchClientes();
    fetchLoans();
    fetchOverdueLoans();
    fetchLateLoans();
  }, []);

  const fetchSummary = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/summary`);
      setSummary(response.data);
    } catch (error) {
      console.error('Error fetching summary:', error);
    }
  };

  const fetchRecentPayments = async () => {
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/recent-payments`);
      setRecentPayments(response.data);
    } catch (error) {
      console.error('Error fetching recent payments:', error);
    }
  };

  const fetchClientes = async () => {
    const res = await axios.get(`${process.env.REACT_APP_API_URL}/clientes`);
    setClientes(res.data);
  };

  const fetchLoans = async () => {
    const res = await axios.get(`${process.env.REACT_APP_API_URL}/loans`);
    setLoans(res.data);
  };

  const fetchOverdueLoans = async () => {
    const res = await axios.get(`${process.env.REACT_APP_API_URL}/loans/overdue`);
    setOverdueLoans(res.data);
  };

  const fetchLateLoans = async () => {
    const res = await axios.get(`${process.env.REACT_APP_API_URL}/loans/late`);
    setLateLoans(res.data);
  };

  const deleteCliente = async (id) => {
    await axios.delete(`${process.env.REACT_APP_API_URL}/clientes/${id}`);
    fetchClientes();
  };

  const deleteLoan = async (id) => {
    await axios.delete(`${process.env.REACT_APP_API_URL}/loans/${id}`);
    fetchLoans();
  };

  return (
    <div className="container mt-5">
      <h1>Dashboard</h1>
      <div className="row mb-5">
        <div className="col-md-4">
          <div className="card text-white bg-primary mb-3">
            <div className="card-body">
              <h5 className="card-title">Total de Préstamos</h5>
              <p className="card-text">{summary.totalLoans}</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-white bg-success mb-3">
            <div className="card-body">
              <h5 className="card-title">Monto Total Prestado</h5>
              <p className="card-text">${summary.totalAmountLoaned}</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-white bg-info mb-3">
            <div className="card-body">
              <h5 className="card-title">Usuarios Registrados</h5>
              <p className="card-text">{summary.totalUsers}</p>
            </div>
          </div>
        </div>
      </div>

      <div className="mb-5">
        <h2>Pagos Recientes</h2>
        <table className="table">
          <thead>
            <tr>
              <th>ID del Préstamo</th>
              <th>Cantidad</th>
              <th>Fecha</th>
            </tr>
          </thead>
          <tbody>
            {recentPayments.map(payment => (
              <tr key={payment.id}>
                <td>{payment.loanId}</td>
                <td>${payment.amount}</td>
                <td>{new Date(payment.date).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="row">
        <div className="col-md-6">
          <h2>Clientes</h2>
          <ul className="list-group">
            {clientes.map(cliente => (
              <li key={cliente._id} className="list-group-item">
                {cliente.name} - {cliente.email}
                <button onClick={() => deleteCliente(cliente._id)} className="btn btn-danger btn-sm float-right ml-2">Eliminar</button>
                <Link to={`/edit-cliente/${cliente._id}`} className="btn btn-warning btn-sm float-right">Editar</Link>
              </li>
            ))}
          </ul>
        </div>
        <div className="col-md-6">
          <h2>Préstamos</h2>
          <ul className="list-group">
            {loans.map(loan => (
              <li key={loan._id} className="list-group-item">
                {loan.amount} - {loan.term} meses
                <button onClick={() => deleteLoan(loan._id)} className="btn btn-danger btn-sm float-right ml-2">Eliminar</button>
                <Link to={`/edit-loan/${loan._id}`} className="btn btn-warning btn-sm float-right">Editar</Link>
              </li>
            ))}
          </ul>
        </div>
      </div>

      <div className="row mt-4">
        <div className="col-md-6">
          <h2>Préstamos en Atraso</h2>
          <ul className="list-group">
            {overdueLoans.map(loan => (
              <li key={loan._id} className="list-group-item">
                {loan.amount} - {loan.term} meses
              </li>
            ))}
          </ul>
        </div>
        <div className="col-md-6">
          <h2>Préstamos en Mora</h2>
          <ul className="list-group">
            {lateLoans.map(loan => (
              <li key={loan._id} className="list-group-item">
                {loan.amount} - {loan.term} meses
              </li>
            ))}
          </ul>
        </div>
      </div>

      <ul className="list-group mt-5">
        <li className="list-group-item">
          <Link to="/register">Registro de Cliente</Link>
        </li>
        <li className="list-group-item">
          <Link to="/loan-request">Solicitud de Préstamo</Link>
        </li>
        <li className="list-group-item">
          <Link to="/payments">Pagos</Link>
        </li>
        <li className="list-group-item">
          <Link to="/late-loans">Préstamos en Atraso</Link>
        </li>
        <li className="list-group-item">
          <Link to="/overdue-loans">Préstamos en Mora</Link>
        </li>
        <li className="list-group-item">
          <Link to="/amortization">Amortización de Préstamos</Link>
        </li>
      </ul>
    </div>
  );
};

