// src/components/OverdueLoans.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OverdueLoans = () => {
  const [loans, setLoans] = useState([]);

  useEffect(() => {
    const fetchLoans = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/loans/overdue`);
        setLoans(response.data);
      } catch (error) {
        console.error('Error fetching overdue loans:', error);
      }
    };
    fetchLoans();
  }, []);

  return (
    <div className="container mt-5">
      <h1>Pr√©stamos en Atraso</h1>
      <ul className="list-group">
        {loans.map((loan) => (
          <li key={loan._id} className="list-group-item">
            {loan.clientName} - {loan.amount} - {loan.dueDate}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OverdueLoans;
