import axios from 'axios';
import React, { useState, useEffect } from 'react';

const Dashboard = () => {
    const [summary, setSummary] = useState({
        totalClientes: 0,
        totalPrestamos: 0,
        prestamosEnMora: 0,
        pagosRecientes: [],
    });

    useEffect(() => {
        const fetchSummary = async () => {
            try {
                const response = await axios.get(`/loans/summary`);
                setSummary(response.data);
            } catch (error) {
                console.error('Error fetching summary:', error);
            }
        };

        fetchSummary();
    }, []);

    return (
        <div className="container mt-5">
            <h1>Dashboard</h1>
            <div className="row">
                <div className="col-md-3">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Total Clientes</h5>
                            <p className="card-text">{summary.totalClientes}</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-3">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Total de Préstamos</h5>
                            <p className="card-text">{summary.totalPrestamos}</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-3">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Préstamos en Mora</h5>
                            <p className="card-text">{summary.prestamosEnMora}</p>
                        </div>
                    </div>
                </div>
            </div>
            <h2 className="mt-5">Pagos Recientes</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Cliente</th>
                        <th>Monto</th>
                        <th>Fecha</th>
                    </tr>
                </thead>
                <tbody>
                    {summary.pagosRecientes.map(pago => (
                        <tr key={pago._id}>
                            <td>{pago.clientId.name}</td>
                            <td>{pago.amount}</td>
                            <td>{new Date(pago.date).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;



