// src/components/OverdueLoans.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OverdueLoans = () => {
  const [overdueLoans, setOverdueLoans] = useState([]);

  useEffect(() => {
    const fetchOverdueLoans = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/loans/overdue`);
        setOverdueLoans(response.data);
      } catch (error) {
        console.error('Error fetching overdue loans:', error);
      }
    };

    fetchOverdueLoans();
  }, []);

  return (
    <div className="container mt-5">
      <h2>Pr√©stamos en Mora</h2>
      <ul className="list-group">
        {overdueLoans.map((loan) => (
          <li key={loan._id} className="list-group-item">
            {loan.name} - {loan.amount}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default OverdueLoans;
