import axios from "axios";
import React, { useState, useEffect } from "react";

const Payments = () => {
  const [form, setForm] = useState({
    loanId: "",
    amount: "",
  });
  const [loans, setLoans] = useState([]);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchLoans = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api
/loans`);
        setLoans(response.data);
      } catch (error) {
        console.error("Error fetching loans:", error);
      }
    };

    fetchLoans();
  }, []);

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/api
/payments`,
        form
      );
      setMessage("Pago registrado con éxito.");
    } catch (error) {
      setMessage("Error al registrar el pago.");
    }
  };

  const getClientName = (loan) => {
    return loan && loan.clientId
      ? `${loan.clientId.name} (${loan.clientId.email})`
      : "indefinido (no definido)";
  };

  return (
    <div className="container mt-5">
      <div className="jumbotron text-center">
        <h1 className="display-4">Registro de Pagos</h1>
        <p className="lead">Complete el formulario para registrar un pago</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="loanId" className="form-label">
            Préstamo
          </label>
          <select
            className="form-control"
            id="loanId"
            name="loanId"
            value={form.loanId}
            onChange={handleChange}
            required
          >
            <option value="">Seleccione un préstamo</option>
            {loans.map((loan) => (
              <option key={loan._id} value={loan._id}>
                {getClientName(loan)} - Monto: {loan.amount}
              </option>
            ))}
          </select>
        </div>
        {form.loanId && (
          <div className="mb-3">
            <label>Cliente</label>
            <p>
              {getClientName(loans.find((loan) => loan._id === form.loanId))}
            </p>
          </div>
        )}
        <div className="mb-3">
          <label htmlFor="amount" className="form-label">
            Monto
          </label>
          <input
            type="number"
            className="form-control"
            id="amount"
            name="amount"
            value={form.amount}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Registrar Pago
        </button>
        {message && <p>{message}</p>}
      </form>
    </div>
  );
};

export default Payments;
