// src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Chart } from 'chart.js';

const Dashboard = () => {
    const [loans, setLoans] = useState([]);
    const [payments, setPayments] = useState([]);
    const [statistics, setStatistics] = useState({});
    const [token, setToken] = useState('');

    useEffect(() => {
        if (token) {
            fetchData();
        }
    }, [token]);

    const fetchData = async () => {
        const headers = { 'x-auth-token': token };

        const responseLoans = await fetch('/api/reports/loans', { headers });
        const loansData = await responseLoans.json();
        setLoans(loansData);

        const responsePayments = await fetch('/api/reports/payments', { headers });
        const paymentsData = await responsePayments.json();
        setPayments(paymentsData);

        const responseStatistics = await fetch('/api/reports/statistics', { headers });
        const statisticsData = await responseStatistics.json();
        setStatistics(statisticsData);

        createCharts(loansData, paymentsData);
    };

    const createCharts = (loansData, paymentsData) => {
        const loansChartCtx = document.getElementById('loansChart').getContext('2d');
        const paymentsChartCtx = document.getElementById('paymentsChart').getContext('2d');

        new Chart(loansChartCtx, {
            type: 'bar',
            data: {
                labels: loansData.map(loan => loan.borrower.name),
                datasets: [{
                    label: 'Loan Amount',
                    data: loansData.map(loan => loan.amount),
                    backgroundColor: 'rgba(75, 192, 192, 0.2)',
                    borderColor: 'rgba(75, 192, 192, 1)',
                    borderWidth: 1
                }]
            },
            options: {
                scales: {
                    y: {
                        beginAtZero: true
                    }
                }
            }
        });

        new Chart(paymentsChartCtx, {
            type: 'line',
            data: {
                labels: paymentsData.map(payment => new Date(payment.paymentDate).toLocaleDateString()),
                datasets: [{
                    label: 'Payment Amount',
                    data: paymentsData.map(payment => payment.amount),
                    backgroundColor: 'rgba(153, 102, 255, 0.2)',
                    borderColor: 'rgba(153, 102, 255, 1)',
                    borderWidth: 1
                }]
            },
            options: {
                scales: {
                    y: {
                        beginAtZero: true
                    }
                }
            }
        });
    };

    return (
        <div className="container">
            <h1>Dashboard</h1>
            <input
                type="text"
                placeholder="Enter JWT Token"
                value={token}
                onChange={(e) => setToken(e.target.value)}
                style={{ width: '300px', padding: '10px' }}
            />
            <button onClick={fetchData}>Fetch Data</button>
            <canvas id="loansChart" width="400" height="200"></canvas>
            <canvas id="paymentsChart" width="400" height="200"></canvas>
        </div>
    );
};

export default Dashboard;
