import React, { useState } from 'react';
import axios from 'axios';

const LoanSearch = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`http://localhost:5000/api/loans/search?query=${searchQuery}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error fetching loans:', error);
    }
  };

  return (
    <div>
      <h2>Buscar Préstamos</h2>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Buscar por cliente o monto"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button type="submit">Buscar</button>
      </form>
      {searchResults.length > 0 && (
        <table className="table table-striped mt-3">
          <thead>
            <tr>
              <th>Cliente</th>
              <th>Monto</th>
              <th>Tasa de Interés</th>
              <th>Plazo</th>
              <th>Frecuencia</th>
              <th>Estado</th>
              <th>Fecha de Vencimiento</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {searchResults.map((loan) => (
              <tr key={loan._id}>
                <td>{loan.clientName}</td>
                <td>{loan.amount}</td>
                <td>{loan.interestRate}</td>
                <td>{loan.term}</td>
                <td>{loan.frequency}</td>
                <td>{loan.status}</td>
                <td>{new Date(loan.dueDate).toLocaleDateString()}</td>
                <td>
                  <button className="btn btn-warning mr-2">Editar</button>
                  <button className="btn btn-danger">Eliminar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default LoanSearch;
