import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useState, useEffect } from 'react';

const Dashboard = () => {
  const [loans, setLoans] = useState([]);
  const [payments, setPayments] = useState([]);

  useEffect(() => {
    // Fetch loans and payments data from the server
    const fetchLoans = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/loans');
        setLoans(response.data);
      } catch (error) {
        console.error('Error fetching loans', error);
      }
    };

    const fetchPayments = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/payments');
        setPayments(response.data);
      } catch (error) {
        console.error('Error fetching payments', error);
      }
    };

    fetchLoans();
    fetchPayments();
  }, []);

  return (
    <div className="container mt-5">
      <h1>Dashboard</h1>
      <div className="row">
        <div className="col-md-6">
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">Registro de Cliente</h5>
              <Link to="/register" className="btn btn-primary">Registrar</Link>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">Solicitud de Préstamo</h5>
              <Link to="/loan-request" className="btn btn-primary">Solicitar</Link>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="card mb-4">
            <div className="card-body">
              <h5 className="card-title">Pagos</h5>
              <Link to="/payments" className="btn btn-primary">Realizar Pago</Link>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-md-12">
          <h3>Resumen de Préstamos</h3>
          <ul className="list-group">
            {loans.map((loan, index) => (
              <li key={index} className="list-group-item">
                {loan.name} - ${loan.amount}
              </li>
            ))}
          </ul>
        </div>
        <div className="col-md-12 mt-4">
          <h3>Pagos Recientes</h3>
          <ul className="list-group">
            {payments.map((payment, index) => (
              <li key={index} className="list-group-item">
                {payment.name} - ${payment.amount}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;



