import axios from 'axios';
import React, { useState, useEffect } from 'react';

const Dashboard = () => {
    const [summary, setSummary] = useState({});
    const [recentPayments, setRecentPayments] = useState([]);
    const [overdueLoans, setOverdueLoans] = useState([]);
    const [lateLoans, setLateLoans] = useState([]);

    useEffect(() => {
        const fetchSummary = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/summary`);
                setSummary(response.data);
            } catch (error) {
                console.error('Error fetching summary:', error);
            }
        };

        const fetchRecentPayments = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/recent-payments`);
                setRecentPayments(response.data);
            } catch (error) {
                console.error('Error fetching recent payments:', error);
            }
        };

        const fetchOverdueLoans = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/loans/overdue`);
                setOverdueLoans(response.data);
            } catch (error) {
                console.error('Error fetching overdue loans:', error);
            }
        };

        const fetchLateLoans = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/loans/late`);
                setLateLoans(response.data);
            } catch (error) {
                console.error('Error fetching late loans:', error);
            }
        };

        fetchSummary();
        fetchRecentPayments();
        fetchOverdueLoans();
        fetchLateLoans();
    }, []);

    return (
        <div className="container mt-5">
            <h1>Dashboard</h1>
            <div className="row">
                <div className="col-md-3">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Total Préstamos</h5>
                            <p className="card-text">{summary.totalLoans}</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-3">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Monto Total Prestado</h5>
                            <p className="card-text">${summary.totalAmount}</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-3">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Clientes Registrados</h5>
                            <p className="card-text">{summary.totalClients}</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-3">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Préstamos en Mora</h5>
                            <p className="card-text">{overdueLoans.length}</p>
                        </div>
                    </div>
                </div>
            </div>
            <h2 className="mt-5">Pagos Recientes</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>ID del Préstamo</th>
                        <th>Fecha de Pago</th>
                        <th>Monto</th>
                    </tr>
                </thead>
                <tbody>
                    {recentPayments.map(payment => (
                        <tr key={payment._id}>
                            <td>{payment.loanId}</td>
                            <td>{new Date(payment.date).toLocaleDateString()}</td>
                            <td>${payment.amount}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <h2 className="mt-5">Préstamos en Mora</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>ID del Préstamo</th>
                        <th>Cliente</th>
                        <th>Fecha de Vencimiento</th>
                    </tr>
                </thead>
                <tbody>
                    {overdueLoans.map(loan => (
                        <tr key={loan._id}>
                            <td>{loan._id}</td>
                            <td>{loan.clientId}</td>
                            <td>{new Date(loan.dueDate).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <h2 className="mt-5">Préstamos en Atraso</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>ID del Préstamo</th>
                        <th>Cliente</th>
                        <th>Fecha de Vencimiento</th>
                    </tr>
                </thead>
                <tbody>
                    {lateLoans.map(loan => (
                        <tr key={loan._id}>
                            <td>{loan._id}</td>
                            <td>{loan.clientId}</td>
                            <td>{new Date(loan.dueDate).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;




