import axios from 'axios';
import React, { useState } from 'react';

const LoanRequest = () => {
    const [form, setForm] = useState({
        clientId: '',
        amount: '',
        interestRate: '',
        term: '',
        frequency: 'daily',
    });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/loans/request', form);
            setMessage('Préstamo solicitado con éxito.');
        } catch (error) {
            setMessage('Error al solicitar el préstamo.');
        }
    };

    return (
        <div className="container mt-5">
            <h1>Solicitud de Préstamo</h1>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="clientId" className="form-label">ID del Cliente</label>
                    <input
                        type="text"
                        className="form-control"
                        id="clientId"
                        name="clientId"
                        value={form.clientId}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="amount" className="form-label">Monto</label>
                    <input
                        type="number"
                        className="form-control"
                        id="amount"
                        name="amount"
                        value={form.amount}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="interestRate" className="form-label">Tasa de Interés (%)</label>
                    <input
                        type="number"
                        className="form-control"
                        id="interestRate"
                        name="interestRate"
                        value={form.interestRate}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="term" className="form-label">Término (días, semanas, meses)</label>
                    <input
                        type="number"
                        className="form-control"
                        id="term"
                        name="term"
                        value={form.term}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label htmlFor="frequency" className="form-label">Frecuencia</label>
                    <select
                        className="form-control"
                        id="frequency"
                        name="frequency"
                        value={form.frequency}
                        onChange={handleChange}
                        required
                    >
                        <option value="daily">Diario</option>
                        <option value="weekly">Semanal</option>
                        <option value="monthly">Mensual</option>
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Solicitar Préstamo</button>
                {message && <p>{message}</p>}
            </form>
        </div>
    );
};

export default LoanRequest;
