import axios from 'axios';
import React, { useState, useEffect } from 'react';

const Dashboard = () => {
    const [summary, setSummary] = useState({});
    const [recentPayments, setRecentPayments] = useState([]);

    useEffect(() => {
        const fetchSummary = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/loans/summary`);
                setSummary(response.data);
            } catch (error) {
                console.error('Error fetching summary:', error);
            }
        };

        const fetchRecentPayments = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/loans/recent-payments`);
                setRecentPayments(response.data);
            } catch (error) {
                console.error('Error fetching recent payments:', error);
            }
        };

        fetchSummary();
        fetchRecentPayments();
    }, []);

    return (
        <div className="container mt-5">
            <h1>Dashboard</h1>
            <div className="row">
                <div className="col-md-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Clientes Totales</h5>
                            <p className="card-text">{summary.totalClients}</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Total de Pr√©stamos</h5>
                            <p className="card-text">{summary.totalLoans}</p>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Pr√©stamos en Mora</h5>
                            <p className="card-text">{summary.loansInArrears}</p>
                        </div>
                    </div>
                </div>
            </div>
            <h2 className="mt-5">Pagos Recientes</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Cliente</th>
                        <th>Monto</th>
                        <th>Fecha</th>
                    </tr>
                </thead>
                <tbody>
                    {recentPayments.map(payment => (
                        <tr key={payment._id}>
                            <td>{payment.loanId.clientId.name} ({payment.loanId.clientId.email})</td>
                            <td>{payment.amount}</td>
                            <td>{new Date(payment.date).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;



