import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';

const Dashboard = () => {
  const history = useHistory();

  const navigateToUsers = () => {
    history.push('/users');
  };

  return (
    <Container>
      <Row className="my-4">
        <Col>
          <Card>
            <Card.Body>
              <Card.Title>Gestión de Préstamos</Card.Title>
              <Card.Text>
                Aquí puedes gestionar todos los préstamos.
              </Card.Text>
              <Button variant="primary">Ir a Préstamos</Button>
            </Card.Body>
          </Card>
        </Col>
        <Col>
          <Card>
            <Card.Body>
              <Card.Title>Gestión de Usuarios</Card.Title>
              <Card.Text>
                Aquí puedes gestionar todos los usuarios.
              </Card.Text>
              <Button variant="primary" onClick={navigateToUsers}>Ir a Usuarios</Button>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Dashboard;



