import React, { useState } from 'react';
import axios from 'axios';

const LoanRequest = () => {
  const [form, setForm] = useState({
    name: '',
    amount: '',
    term: '',
  });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/loans`, form);
      setMessage('Solicitud de préstamo enviada con éxito.');
    } catch (error) {
      setMessage('Error al enviar la solicitud de préstamo.');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Solicitud de Préstamo</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">Nombre</label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            value={form.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="amount" className="form-label">Cantidad</label>
          <input
            type="number"
            className="form-control"
            id="amount"
            name="amount"
            value={form.amount}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label htmlFor="term" className="form-label">Plazo (meses)</label>
          <input
            type="number"
            className="form-control"
            id="term"
            name="term"
            value={form.term}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">Enviar Solicitud</button>
      </form>
      {message && <div className="mt-3 alert alert-info">{message}</div>}
    </div>
  );
};

export default LoanRequest;

