import axios from "axios";
import React, { useState, useEffect } from "react";

const Dashboard = () => {
  const [summary, setSummary] = useState({});
  const [recentPayments, setRecentPayments] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const fetchSummary = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/reports/summary");
        setSummary(response.data);
      } catch (error) {
        console.error("Error fetching summary:", error);
      }
    };

    const fetchRecentPayments = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/reports/recent-payments");
        setRecentPayments(response.data);
      } catch (error) {
        console.error("Error fetching recent payments:", error);
      }
    };

    fetchSummary();
    fetchRecentPayments();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`http://localhost:5000/api/clientes/search?query=${searchQuery}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error("Error fetching search results:", error);
    }
  };

  return (
    <div className="container mt-5">
      <div className="jumbotron text-center">
        <h1 className="display-4">Dashboard</h1>
        <p className="lead">Resumen de Préstamos y Pagos</p>
      </div>
      <div className="row">
        <div className="col-md-4">
          <div className="card text-white bg-primary mb-3">
            <div className="card-header">Clientes totales</div>
            <div className="card-body">
              <h5 className="card-title">{summary.totalClientes || 0}</h5>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-white bg-success mb-3">
            <div className="card-header">Total de Préstamos</div>
            <div className="card-body">
              <h5 className="card-title">{summary.totalPrestamos || 0}</h5>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card text-white bg-danger mb-3">
            <div className="card-header">Préstamos en Mora</div>
            <div className="card-body">
              <h5 className="card-title">{summary.totalMora || 0}</h5>
            </div>
          </div>
        </div>
      </div>
      <h2>Pagos Recientes</h2>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Cliente</th>
            <th>Monto</th>
            <th>Fecha</th>
          </tr>
        </thead>
        <tbody>
          {recentPayments.map((payment) => (
            <tr key={payment._id}>
              <td>{payment.client ? payment.client.name : "Cliente no encontrado"}</td>
              <td>{payment.amount}</td>
              <td>{payment.date ? new Date(payment.date).toLocaleDateString() : "Fecha inválida"}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Buscar Cliente</h2>
      <form onSubmit={handleSearch}>
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="Buscar por nombre o cédula"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">Buscar</button>
      </form>
      <h2>Resultados de la Búsqueda</h2>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Correo Electrónico</th>
            <th>Teléfono</th>
            <th>Cédula</th>
            <th>Dirección</th>
          </tr>
        </thead>
        <tbody>
          {searchResults.map((cliente) => (
            <tr key={cliente._id}>
              <td>{cliente.name}</td>
              <td>{cliente.email}</td>
              <td>{cliente.phone}</td>
              <td>{cliente.idNumber}</td>
              <td>{cliente.address}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;


