import React from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const LoanRequest = () => {
  return (
    <div className="loan-request-container">
      <h2>Solicitud de Préstamo</h2>
      <Formik
        initialValues={{ amount: '', term: '', purpose: '' }}
        validationSchema={Yup.object({
          amount: Yup.number().required('Required'),
          term: Yup.string().required('Required'),
          purpose: Yup.string().required('Required')
        })}
        onSubmit={(values, { setSubmitting }) => {
          setTimeout(() => {
            console.log(values);
            setSubmitting(false);
          }, 400);
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <div>
              <label htmlFor="amount">Monto</label>
              <Field name="amount" type="text" />
              <ErrorMessage name="amount" component="div" />
            </div>
            <div>
              <label htmlFor="term">Plazo</label>
              <Field name="term" type="text" />
              <ErrorMessage name="term" component="div" />
            </div>
            <div>
              <label htmlFor="purpose">Propósito</label>
              <Field name="purpose" type="text" />
              <ErrorMessage name="purpose" component="div" />
            </div>
            <button type="submit" disabled={isSubmitting}>Solicitar Préstamo</button>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default LoanRequest;
