import React, { useEffect, useState } from 'react';
import axios from 'axios';

const LateLoans = () => {
  const [lateLoans, setLateLoans] = useState([]);

  useEffect(() => {
    const fetchLateLoans = async () => {
      try {
        const response = await axios.get(`${process.env.REACT_APP_API_URL}/loans/late`);
        setLateLoans(response.data);
      } catch (error) {
        console.error('Error fetching late loans', error);
      }
    };

    fetchLateLoans();
  }, []);

  return (
    <div className="container mt-5">
      <h1>Pr√©stamos en Mora</h1>
      <ul className="list-group">
        {lateLoans.map((loan) => (
          <li key={loan.id} className="list-group-item">
            {loan.clientName} - {loan.amount} - {loan.dueDate}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LateLoans;
