import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [loans, setLoans] = useState([]);
  const [editClient, setEditClient] = useState(null);

  useEffect(() => {
    fetchClients();
    fetchLoans();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/clients");
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  const fetchLoans = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/loans");
      setLoans(response.data);
    } catch (error) {
      console.error("Error fetching loans:", error);
    }
  };

  const handleDeleteClient = async (clientId) => {
    if (window.confirm("¿Estás seguro de que deseas eliminar este cliente?")) {
      try {
        await axios.delete(`http://localhost:5000/api/clients/${clientId}`);
        fetchClients();
      } catch (error) {
        console.error("Error deleting client:", error);
      }
    }
  };

  const handleEditClient = (client) => {
    setEditClient(client);
  };

  const handleUpdateClient = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/clients/${editClient._id}`, editClient);
      setEditClient(null);
      fetchClients();
    } catch (error) {
      console.error("Error updating client:", error);
    }
  };

  return (
    <div>
      <h2>Clientes</h2>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Correo Electrónico</th>
            <th>Teléfono</th>
            <th>Cédula</th>
            <th>Dirección</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {clients.map((client) => (
            <tr key={client._id}>
              <td>{client.name}</td>
              <td>{client.email}</td>
              <td>{client.phone}</td>
              <td>{client.cedula}</td>
              <td>{client.address}</td>
              <td>
                <button className="btn btn-warning mr-2" onClick={() => handleEditClient(client)}>
                  Editar
                </button>
                <button className="btn btn-danger" onClick={() => handleDeleteClient(client._id)}>
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editClient && (
        <div>
          <h3>Editar Cliente</h3>
          <form onSubmit={handleUpdateClient}>
            <div className="form-group">
              <label>Nombre</label>
              <input
                type="text"
                className="form-control"
                value={editClient.name}
                onChange={(e) => setEditClient({ ...editClient, name: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Correo Electrónico</label>
              <input
                type="email"
                className="form-control"
                value={editClient.email}
                onChange={(e) => setEditClient({ ...editClient, email: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Teléfono</label>
              <input
                type="text"
                className="form-control"
                value={editClient.phone}
                onChange={(e) => setEditClient({ ...editClient, phone: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Cédula</label>
              <input
                type="text"
                className="form-control"
                value={editClient.cedula}
                onChange={(e) => setEditClient({ ...editClient, cedula: e.target.value })}
              />
            </div>
            <div className="form-group">
              <label>Dirección</label>
              <input
                type="text"
                className="form-control"
                value={editClient.address}
                onChange={(e) => setEditClient({ ...editClient, address: e.target.value })}
              />
            </div>
            <button type="submit" className="btn btn-primary">Actualizar Cliente</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Dashboard;




