import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Dashboard = () => {
    const [summary, setSummary] = useState({});
    const [recentPayments, setRecentPayments] = useState([]);

    useEffect(() => {
        const fetchSummary = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/summary`);
                setSummary(response.data);
            } catch (error) {
                console.error('Error fetching summary:', error);
            }
        };

        const fetchRecentPayments = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/recent-payments`);
                setRecentPayments(response.data);
            } catch (error) {
                console.error('Error fetching recent payments:', error);
            }
        };

        fetchSummary();
        fetchRecentPayments();
    }, []);

    return (
        <div className="container mt-5">
            <h1>Dashboard</h1>
            <div className="row">
                <div className="col-md-4">
                    <div className="card text-white bg-primary mb-3">
                        <div className="card-header">Total Clientes</div>
                        <div className="card-body">
                            <h5 className="card-title">{summary.totalClients}</h5>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card text-white bg-success mb-3">
                        <div className="card-header">Total Préstamos</div>
                        <div className="card-body">
                            <h5 className="card-title">{summary.totalLoans}</h5>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card text-white bg-danger mb-3">
                        <div className="card-header">Préstamos en Mora</div>
                        <div className="card-body">
                            <h5 className="card-title">{summary.loansInArrears}</h5>
                        </div>
                    </div>
                </div>
            </div>
            <h2>Pagos Recientes</h2>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Cliente</th>
                        <th>Monto</th>
                        <th>Fecha</th>
                    </tr>
                </thead>
                <tbody>
                    {recentPayments.map(payment => (
                        <tr key={payment._id}>
                            <td>{payment.loanId.clientId.name}</td>
                            <td>{payment.amount}</td>
                            <td>{new Date(payment.date).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;




