import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
    const [clientes, setClientes] = useState([]);
    const [loans, setLoans] = useState([]);
    const [overdueLoans, setOverdueLoans] = useState([]);
    const [lateLoans, setLateLoans] = useState([]);

    useEffect(() => {
        fetchClientes();
        fetchLoans();
        fetchOverdueLoans();
        fetchLateLoans();
    }, []);

    const fetchClientes = async () => {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/clientes`);
        setClientes(res.data);
    };

    const fetchLoans = async () => {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/loans`);
        setLoans(res.data);
    };

    const fetchOverdueLoans = async () => {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/loans/overdue`);
        setOverdueLoans(res.data);
    };

    const fetchLateLoans = async () => {
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/loans/late`);
        setLateLoans(res.data);
    };

    const deleteCliente = async (id) => {
        await axios.delete(`${process.env.REACT_APP_API_URL}/clientes/${id}`);
        fetchClientes();
    };

    const deleteLoan = async (id) => {
        await axios.delete(`${process.env.REACT_APP_API_URL}/loans/${id}`);
        fetchLoans();
    };

    return (
        <div className="container mt-5">
            <h1>Dashboard</h1>
            <div className="row">
                <div className="col-md-6">
                    <h2>Clientes</h2>
                    <ul className="list-group">
                        {clientes.map(cliente => (
                            <li key={cliente._id} className="list-group-item">
                                {cliente.name} - {cliente.email}
                                <button onClick={() => deleteCliente(cliente._id)} className="btn btn-danger btn-sm float-right ml-2">Eliminar</button>
                                <Link to={`/edit-cliente/${cliente._id}`} className="btn btn-warning btn-sm float-right">Editar</Link>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="col-md-6">
                    <h2>Pr√©stamos</h2>
                    <ul className="list-group">
                        {loans.map(loan => (
                            <li key={loan._id} className="list-group-item">
                                {loan.amount} - {loan.term} meses
                                <button onClick={() => deleteLoan(loan._id)} className="btn btn-danger btn-sm float-right ml-2">Eliminar</button>
                                <Link to={`/edit-loan/${loan._id}`} className="btn btn-warning btn-sm float-right">Editar</Link>
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            <div className="row mt-4">
                <div className="col-md-6">
                    <h2>Pr√©stamos en Atraso</h2>
                    <ul className="list-group">
                        {overdueLoans.map(loan => (
                            <li key={loan._id} className="list-group-item">
                                {loan.amount} - {loan.term} meses
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="col-md-6">
                    <h2>Pr√©stamos en Mora</h2>
                    <ul className="list-group">
                        {lateLoans.map(loan => (
                            <li key={loan._id} className="list-group-item">
                                {loan.amount} - {loan.term} meses
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            <div className="row mt-4">
                <div className="col-md-12">
                    <h2>Registro de Cliente</h2>
                    <Link to="/register" className="btn btn-primary">Registrar Cliente</Link>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;



