import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
    const [summary, setSummary] = useState({});
    const [recentPayments, setRecentPayments] = useState([]);
    const [lateLoans, setLateLoans] = useState([]);
    const [overdueLoans, setOverdueLoans] = useState([]);

    useEffect(() => {
        const fetchSummary = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/summary`);
                setSummary(response.data);
            } catch (error) {
                console.error('Error fetching summary:', error);
            }
        };

        const fetchRecentPayments = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/recent-payments`);
                setRecentPayments(response.data);
            } catch (error) {
                console.error('Error fetching recent payments:', error);
            }
        };

        const fetchLateLoans = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/late-loans`);
                setLateLoans(response.data);
            } catch (error) {
                console.error('Error fetching late loans:', error);
            }
        };

        const fetchOverdueLoans = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/overdue-loans`);
                setOverdueLoans(response.data);
            } catch (error) {
                console.error('Error fetching overdue loans:', error);
            }
        };

        fetchSummary();
        fetchRecentPayments();
        fetchLateLoans();
        fetchOverdueLoans();
    }, []);

    return (
        <div className="container mt-5">
            <h1>Dashboard</h1>
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">Resumen de Préstamos</h5>
                    <p className="card-text">Total de Préstamos: {summary.totalLoans}</p>
                    <p className="card-text">Monto Total Prestado: {summary.totalAmount}</p>
                    <p className="card-text">Número de Usuarios Registrados: {summary.totalUsers}</p>
                </div>
            </div>
            <div className="mt-4">
                <h5>Pagos Recientes</h5>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID del Préstamo</th>
                            <th>Monto</th>
                            <th>Fecha</th>
                        </tr>
                    </thead>
                    <tbody>
                        {recentPayments.map((payment) => (
                            <tr key={payment._id}>
                                <td>{payment.loanId}</td>
                                <td>{payment.amount}</td>
                                <td>{payment.date}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="mt-4">
                <h5>Préstamos en Atraso</h5>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID del Préstamo</th>
                            <th>Cliente</th>
                            <th>Monto</th>
                            <th>Fecha de Vencimiento</th>
                        </tr>
                    </thead>
                    <tbody>
                        {lateLoans.map((loan) => (
                            <tr key={loan._id}>
                                <td>{loan._id}</td>
                                <td>{loan.clientId}</td>
                                <td>{loan.amount}</td>
                                <td>{loan.dueDate}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div className="mt-4">
                <h5>Préstamos en Mora</h5>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID del Préstamo</th>
                            <th>Cliente</th>
                            <th>Monto</th>
                            <th>Fecha de Vencimiento</th>
                        </tr>
                    </thead>
                    <tbody>
                        {overdueLoans.map((loan) => (
                            <tr key={loan._id}>
                                <td>{loan._id}</td>
                                <td>{loan.clientId}</td>
                                <td>{loan.amount}</td>
                                <td>{loan.dueDate}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Dashboard;


