import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
    const [summary, setSummary] = useState({});
    const [recentPayments, setRecentPayments] = useState([]);
    const [loans, setLoans] = useState([]);

    useEffect(() => {
        fetchSummary();
        fetchRecentPayments();
        fetchLoans();
    }, []);

    const fetchSummary = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/reports/summary');
            setSummary(response.data);
        } catch (error) {
            console.error('Error fetching summary:', error);
        }
    };

    const fetchRecentPayments = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/reports/recent-payments');
            setRecentPayments(response.data);
        } catch (error) {
            console.error('Error fetching recent payments:', error);
        }
    };

    const fetchLoans = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/loans');
            setLoans(response.data);
        } catch (error) {
            console.error('Error fetching loans:', error);
        }
    };

    const handleDeleteLoan = async (loanId) => {
        if (window.confirm('¿Estás seguro de que deseas eliminar este préstamo?')) {
            try {
                await axios.delete(`http://localhost:5000/api/loans/${loanId}`);
                fetchLoans(); // Refresh the loans list
            } catch (error) {
                console.error('Error deleting loan:', error);
            }
        }
    };

    return (
        <div className="container">
            <h1>Dashboard</h1>
            <h2>Resumen de Préstamos y Pagos</h2>
            <div className="row">
                <div className="col">
                    <div className="card text-white bg-primary mb-3">
                        <div className="card-body">
                            <h5 className="card-title">Clientes totales</h5>
                            <p className="card-text">{summary.totalClients}</p>
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="card text-white bg-success mb-3">
                        <div className="card-body">
                            <h5 className="card-title">Total de Préstamos</h5>
                            <p className="card-text">{summary.totalLoans}</p>
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="card text-white bg-danger mb-3">
                        <div className="card-body">
                            <h5 className="card-title">Préstamos en Mora</h5>
                            <p className="card-text">{summary.overdueLoans}</p>
                        </div>
                    </div>
                </div>
            </div>
            <h2>Pagos Recientes</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Cliente</th>
                        <th>Monto</th>
                        <th>Fecha</th>
                    </tr>
                </thead>
                <tbody>
                    {recentPayments.map(payment => (
                        <tr key={payment._id}>
                            <td>{payment.client ? payment.client.name : 'Cliente no encontrado'}</td>
                            <td>{payment.amount}</td>
                            <td>{new Date(payment.date).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <h2>Préstamos</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Cliente</th>
                        <th>Monto</th>
                        <th>Interés</th>
                        <th>Plazo</th>
                        <th>Frecuencia</th>
                        <th>Estado</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {loans.map(loan => (
                        <tr key={loan._id}>
                            <td>{loan.client ? loan.client.name : 'Cliente no encontrado'}</td>
                            <td>{loan.amount}</td>
                            <td>{loan.interestRate}%</td>
                            <td>{loan.term}</td>
                            <td>{loan.frequency}</td>
                            <td>{loan.status}</td>
                            <td>
                                <button className="btn btn-warning btn-sm">Editar</button>
                                <button 
                                    className="btn btn-danger btn-sm" 
                                    onClick={() => handleDeleteLoan(loan._id)}
                                >
                                    Eliminar
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;


