import axios from "axios";
import React, { useState, useEffect } from "react";

const Loans = () => {
  const [loans, setLoans] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  const fetchLoans = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/loans");
      setLoans(response.data);
    } catch (error) {
      console.error("Error fetching loans:", error);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`http://localhost:5000/api/loans/search?query=${searchQuery}`);
      setLoans(response.data);
    } catch (error) {
      console.error("Error fetching search results:", error);
    }
  };

  const handleDeleteLoan = async (loanId) => {
    if (window.confirm("¿Estás seguro de que deseas eliminar este préstamo?")) {
      try {
        await axios.delete(`http://localhost:5000/api/loans/delete/${loanId}`);
        fetchLoans();
      } catch (error) {
        console.error("Error deleting loan:", error);
      }
    }
  };

  useEffect(() => {
    fetchLoans();
  }, []);

  return (
    <div className="container mt-5">
      <h2>Préstamos</h2>
      <form onSubmit={handleSearch}>
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="Buscar por cliente o estado"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">Buscar</button>
        <button type="button" className="btn btn-info ml-2" onClick={fetchLoans}>Cargar Préstamos</button>
      </form>
      <table className="table table-striped mt-3">
        <thead>
          <tr>
            <th>Cliente</th>
            <th>Monto</th>
            <th>Tasa de Interés</th>
            <th>Plazo</th>
            <th>Frecuencia</th>
            <th>Estado</th>
            <th>Fecha de Vencimiento</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {loans.map((loan) => (
            <tr key={loan._id}>
              <td>{loan.clientId ? loan.clientId.name : "Cliente no encontrado"}</td>
              <td>{loan.amount}</td>
              <td>{loan.interestRate}</td>
              <td>{loan.term}</td>
              <td>{loan.frequency}</td>
              <td>{loan.status}</td>
              <td>{new Date(loan.dueDate).toLocaleDateString()}</td>
              <td>
                <button className="btn btn-warning mr-2">Editar</button>
                <button className="btn btn-danger" onClick={() => handleDeleteLoan(loan._id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Loans;


