      setEditLoan(null);
      setLoanForm({ clientId: '', amount: '', interestRate: '', term: '', frequency: '', status: '', dueDate: '' });
      fetchLoans();
    } catch (error) {
      console.error("Error updating loan:", error);
    }
  };

