            <th>Monto</th>
            <th>Fecha</th>
          </tr>
        </thead>
        <tbody>
          {recentPayments.map((payment) => (
            <tr key={payment._id}>
              <td>{payment.client ? payment.client.name : "Cliente no encontrado"}</td>
              <td>{payment.amount}</td>
              <td>{payment.date ? new Date(payment.date).toLocaleDateString() : "Fecha inválida"}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h2>Buscar Cliente</h2>
      <form onSubmit={handleSearch}>
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="Buscar por nombre o cédula"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">Buscar</button>
      </form>
      <h2>Resultados de la Búsqueda</h2>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Correo Electrónico</th>
            <th>Teléfono</th>
            <th>Cédula</th>
            <th>Dirección</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {searchResults.map((cliente) => (
            <tr key={cliente._id}>
              <td>{cliente.name}</td>
              <td>{cliente.email}</td>
              <td>{cliente.phone}</td>
              <td>{cliente.idNumber}</td>
              <td>{cliente.address}</td>
              <td>
                <button className="btn btn-warning mr-2" onClick={() => handleEdit(cliente)}>Editar</button>
                <button className="btn btn-danger" onClick={() => handleDelete(cliente._id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {editClient && (
        <div className="mt-5">
          <h2>Editar Cliente</h2>
          <form onSubmit={(e) => { e.preventDefault(); handleUpdate(); }}>
            <div className="form-group">
              <label>Nombre</label>
              <input
