import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [clients, setClients] = useState([]);
  const [loans, setLoans] = useState([]);
  const [recentPayments, setRecentPayments] = useState([]);
  const [editClient, setEditClient] = useState(null);
  const [editLoan, setEditLoan] = useState(null);

  useEffect(() => {
    fetchClients();
    fetchLoans();
    fetchRecentPayments();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/clients');
      setClients(response.data);
    } catch (error) {
      console.error("Error fetching clients:", error);
    }
  };

  const fetchLoans = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/loans');
      setLoans(response.data);
    } catch (error) {
      console.error("Error fetching loans:", error);
    }
  };

  const fetchRecentPayments = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/payments/recent');
      setRecentPayments(response.data);
    } catch (error) {
      console.error("Error fetching recent payments:", error);
    }
  };

  const handleEditClient = (client) => {
    setEditClient(client);
  };

  const handleEditLoan = (loan) => {
    setEditLoan(loan);
  };

  const handleDeleteLoan = async (loanId) => {
    if (window.confirm("¿Estás seguro de que deseas eliminar este préstamo?")) {
      try {
        await axios.delete(`http://localhost:5000/api/loans/${loanId}`);
        fetchLoans();
      } catch (error) {
        console.error("Error deleting loan:", error);
      }
    }
  };

  const handleUpdateClient = async () => {
    try {
      await axios.put(`http://localhost:5000/api/clients/${editClient._id}`, editClient);
      fetchClients();
      setEditClient(null);
    } catch (error) {
      console.error("Error updating client:", error);
    }
  };

  const handleUpdateLoan = async () => {
    try {
      await axios.put(`http://localhost:5000/api/loans/${editLoan._id}`, editLoan);
      fetchLoans();
      setEditLoan(null);
    } catch (error) {
      console.error("Error updating loan:", error);
    }
  };

  return (
    <div>
      <h2>Buscar Cliente</h2>
      <input type="text" placeholder="Buscar por nombre o cédula" />
      <button>Buscar</button>

      <h2>Resultados de la Búsqueda</h2>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Correo Electrónico</th>
            <th>Teléfono</th>
            <th>Cédula</th>
            <th>Dirección</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {clients.map((client) => (
            <tr key={client._id}>
              <td>{client.name}</td>
              <td>{client.email}</td>
              <td>{client.phone}</td>
              <td>{client.idNumber}</td>
              <td>{client.address}</td>
              <td>
                <button className="btn btn-warning mr-2" onClick={() => handleEditClient(client)}>Editar</button>
                <button className="btn btn-danger" onClick={() => handleDeleteClient(client._id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editClient && (
        <div className="mt-5">
          <h2>Editar Cliente</h2>
          <form onSubmit={(e) => { e.preventDefault(); handleUpdateClient(); }}>
            <div className="form-group">
              <label>Nombre</label>
              <input
                type="text"
                className="form-control"
                value={editClient.name}
                onChange={(e) => setEditClient({ ...editClient, name: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Correo Electrónico</label>
              <input
                type="email"
                className="form-control"
                value={editClient.email}
                onChange={(e) => setEditClient({ ...editClient, email: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Teléfono</label>
              <input
                type="text"
                className="form-control"
                value={editClient.phone}
                onChange={(e) => setEditClient({ ...editClient, phone: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Cédula</label>
              <input
                type="text"
                className="form-control"
                value={editClient.idNumber}
                onChange={(e) => setEditClient({ ...editClient, idNumber: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Dirección</label>
              <input
                type="text"
                className="form-control"
                value={editClient.address}
                onChange={(e) => setEditClient({ ...editClient, address: e.target.value })} />
            </div>
            <button type="submit" className="btn btn-success">Actualizar</button>
            <button type="button" className="btn btn-secondary ml-2" onClick={() => setEditClient(null)}>Cancelar</button>
          </form>
        </div>
      )}

      <h2>Préstamos</h2>
      <button className="btn btn-info mb-2" onClick={fetchLoans}>Cargar Préstamos</button>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>Cliente</th>
            <th>Monto</th>
            <th>Tasa de Interés</th>
            <th>Plazo</th>
            <th>Frecuencia</th>
            <th>Estado</th>
            <th>Fecha de Vencimiento</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {loans.map((loan) => (
            <tr key={loan._id}>
              <td>{loan.clientId.name}</td>
              <td>{loan.amount}</td>
              <td>{loan.interestRate}</td>
              <td>{loan.term}</td>
              <td>{loan.frequency}</td>
              <td>{loan.status}</td>
              <td>{new Date(loan.dueDate).toLocaleDateString()}</td>
              <td>
                <button className="btn btn-warning mr-2" onClick={() => handleEditLoan(loan)}>Editar</button>
                <button className="btn btn-danger" onClick={() => handleDeleteLoan(loan._id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editLoan && (
        <div className="mt-5">
          <h2>Editar Préstamo</h2>
          <form onSubmit={(e) => { e.preventDefault(); handleUpdateLoan(); }}>
            <div className="form-group">
              <label>ID del Cliente</label>
              <input
                type="text"
                className="form-control"
                value={editLoan.clientId}
                onChange={(e) => setEditLoan({ ...editLoan, clientId: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Monto</label>
              <input
                type="number"
                className="form-control"
                value={editLoan.amount}
                onChange={(e) => setEditLoan({ ...editLoan, amount: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Tasa de Interés</label>
              <input
                type="number"
                className="form-control"
                value={editLoan.interestRate}
                onChange={(e) => setEditLoan({ ...editLoan, interestRate: e.target.value })} />
            </div>
            <div className="form-group">
              <label>Plazo</label>
              <input
                type="number"
